import requestsfrom bs4 import BeautifulSoupimport timeimport threadingdef read_and_thread(url):    """    this function read a url and write it into a file    :param url: url of site    """    get_html_page = requests.get(url)    html_content = get_html_page.content    soup_content = BeautifulSoup(html_content, "html.parser")    title = soup_content.title.get_text()    print(title)    file.write(soup_content.encode('utf-8'))    print(soup_content)def main():    start_time = time.perf_counter()  # Start The Timer    urls_list = ["https://www.epicgames.com/store/en-US/", "https://store.steampowered.com/",             "https://13news.co.il/writer/tentv_writer_1569374523/",             "https://www.ynet.co.il/home/0,7340,L-8,00.html",             "https://www.rishonlezion.muni.il/Pages/default.aspx",             "https://www.bmw.co.il/he/index.html",             "https://www.apple.com/",             "https://stackoverflow.com/",             "https://www.maserati.com/il/he",             "https://www.ferrari.com/en-IL"]    global file # global value for use in function    file = open("file.txt", "wb")    t1 = threading.Thread(target=read_and_thread, args=(urls_list[0],))    t2 = threading.Thread(target=read_and_thread, args=(urls_list[1],))    t3 = threading.Thread(target=read_and_thread, args=(urls_list[2],))    t4 = threading.Thread(target=read_and_thread, args=(urls_list[3],))    t5 = threading.Thread(target=read_and_thread, args=(urls_list[4],))    t6 = threading.Thread(target=read_and_thread, args=(urls_list[5],))    t7 = threading.Thread(target=read_and_thread, args=(urls_list[6],))    t8 = threading.Thread(target=read_and_thread, args=(urls_list[7],))    t9 = threading.Thread(target=read_and_thread, args=(urls_list[8],))    t10 = threading.Thread(target=read_and_thread, args=(urls_list[9],))    t1.start()    t2.start()    t3.start()    t4.start()    t5.start()    t6.start()    t7.start()    t8.start()    t9.start()    t10.start()    t1.join()    t2.join()    t3.join()    t4.join()    t5.join()    t6.join()    t7.join()    t8.join()    t9.join()    t10.join()    file.close()    end_time = time.perf_counter()  # Stop The Timer    print (f"It takes {end_time-start_time} seconds!")if __name__ == '__main__':    main()